var convict = require('./lib/convict.js');

var check = require('validator').check;

var conf = convict({
    key: {
      doc: "API key",
      format: function (val) {
        check(val, 'key should be in hex').regex(/[a-fA-F0-9]{64}/);
      },
      default: '3cec609c9bc601c047af917a544645c50caf8cd606806b4e0a23312441014deb'
    }
  });

conf.set('key', 'foo');

conf.validate();
